import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders } from '@angular/common/http';
import { JwtHelperService } from '@auth0/angular-jwt';

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  authToken: any;
  user: any;
  constructor(private http: HttpClient, private jwtHelper: JwtHelperService) { }

  registerUser(user: any) 
  {
    const headers = new HttpHeaders().set('Content-Type', 'application/json');
    return this.http.post('http://localhost:3000/api/users/signup', user, { headers });
  }

  createPost()
  {
    this.loadToken();    
    const headers = new HttpHeaders();
    headers.append('Authorization', this.authToken);
    headers.append('Content-Type', 'application/json');
    this.http.post('https://localhost:3000/api/posts/new', {headers});
  }

  isTokenExpired(token: string): boolean {
    return this.jwtHelper.isTokenExpired(token);
  }

  loggedIn()
  {
    if (this.isTokenExpired(this.authToken)) {
      return false;
    } else {
      return true;
    }
  }

  authenticateUser(user:any) {
    const headers = new HttpHeaders().set('Content-Type', 'application/json');
    return this.http.post('http://localhost:3000/api/auth/login', user, { headers });
  }

  storeToken(token:any)
  {
      localStorage.setItem('id_token',token);
      this.authToken=token;
  }

  loadToken()
  {
    const token  = localStorage.getItem('id_token');
    this.authToken=token;
  }

  logout()
  {
    this.authToken=null;
    localStorage.clear();
  }
}
