import { Injectable } from '@angular/core';
import { HttpClient, HttpHeaders, HttpResponse } from '@angular/common/http';
import { Observable } from 'rxjs';

@Injectable({
  providedIn: 'root'
})
export class AuthService {
  constructor(private http: HttpClient) {}

  registerUser(user: any): Observable<HttpResponse<any>> {
    const headers = new HttpHeaders().set('Content-Type', 'application/json');
    return this.http.post('https://localhost:3000/api/users/signup', user, {
      headers,
      observe: 'response', // Set to 'response' to get the full HTTP response
      withCredentials: true, // If you need to include cookies
      // rejectUnauthorized: false, // Uncomment this line only for local development with self-signed certificates
    });
  }
}
