import { Component } from '@angular/core';
import { AuthService } from 'src/app/services/auth.service';

@Component({
  selector: 'app-viewposts',
  templateUrl: './viewposts.component.html',
  styleUrls: ['./viewposts.component.css']
})
export class ViewpostsComponent {
  isTableVisible = false;
  postData:Array<any> = [];


  constructor(private authService:AuthService)
  {
    this.authService.getAllPosts().subscribe(
      (response)=>{
        console.log(response)
        this.postData=response;
      }
    )
  }

  showTable() {

    this.isTableVisible = !this.isTableVisible;
  }
}
<table class="table table-bordered" *ngIf="isTableVisible">
  <thead>
    <tr>
      <th>Title</th>
      <th>Description</th>
      <th>Department Code</th>
      <th>Action</th> <!-- Add a new column for the button -->
    </tr>
  </thead>
  <tbody>
    <tr *ngFor="let dt of postData">
      <td>{{dt.title}}</td>
      <td>{{dt.description}}</td>
      <td>{{dt.departmentCode}}</td>
      <td>
        <button class="btn btn-primary" (click)="yourFunction(dt)">Action</button>
      </td>
    </tr>
  </tbody>
</table>
